INSERT OR IGNORE INTO Breweries (Id, Name, Address1, Address2, City, State, Code, Country, Phone, Website, Filepath, Description) VALUES ('1282ae91d4714281a1b5ec321ca571c4', 'Great Divide Brewing', '2201 Arapahoe Street', '', 'Denver', 'Colorado', '80205', 'United States', '1-303-296-9460', 'http://www.greatdivide.com/', '', 'Since 1994, Great Divide has brewed the most balanced yet assertive and flavorful beers possible.  This commitment has not gone unrewarded  Great Divide has racked up 12 Great American Beer Festival Medals and four World Beer Cup awards, and was recently honored as the 23rd Best Brewer in the World in Ratebeer.com''s World''s Top 100 Brewers, (2007). See for yourself what the excitement is about  and then introduce your friends to the new frontier of beer. --http://www.greatdivide.com');
