INSERT OR IGNORE INTO Breweries (Id, Name, Address1, Address2, City, State, Code, Country, Phone, Website, Filepath, Description) VALUES ('1cd8f27644384428bb7bf05cfd5c20bb', 'Brauhaus Brew Hall', '111 Main Street', '', 'Lucan', 'Minnesota', '56255', 'United States', '1-507-747-2796', 'http://www.braubrothersbrewing.com/', '', 'We are very excited to introduce Brau Brothers Brewing Company, LLC. We are a locally owned and operated brewery, producing strictly for a local and regional market. We like to stay close to the beer we produce as well as to the people who drink it. Our modest scale allows us to be more flexible, dynamic, and closer to retailers as well as beer drinkers.');
