INSERT OR IGNORE INTO Breweries (Id, Name, Address1, Address2, City, State, Code, Country, Phone, Website, Filepath, Description) VALUES ('21fb36d515a046c3838ba77d0d71ffc1', 'Wedge Brewery', '125B Roberts St', '', 'Asheville', 'NC', '28801-3128', 'United States', '828-505-2792', 'http://wedgebrewing.com/', 'wedge.png', 'The Wedge Brewing Co. is located in the lower level of the Wedge Studios situated in the River Arts District of Asheville, N.C. The building is a classic brick warehouse adjacent  to the railroad tracks. The original use of the structure was probably as a food warehouse. The space the brewery is in at onetime stored slaughtered hogs.\n    We chose this location because it is in the last frontier of Asheville.\n    We chose this specific space because it is a perfect fit for our brewing system and gives\nus great outdoor potential.');
