INSERT OR IGNORE INTO Breweries (Id, Name, Address1, Address2, City, State, Code, Country, Phone, Website, Filepath, Description) VALUES ('29b12f46b7cf4af6a84523e650f3a28d', 'August Schell Brewing', '1860 Schell Road', '', 'New Ulm', 'Minnesota', '56073', 'United States', '800-770-5020', 'http://www.schellsbrewery.com/', '', 'The August Schell Brewing Company is a brewing company in New Ulm, Minnesota. It was founded by German immigrants August Schell and Jacob Bernhardt in 1860 and passed into the possession of the Schell family in 1866. It is the second oldest family-owned brewery in America and became the oldest and largest brewery in Minnesota when the company bought the Grain Belt rights in 2002.');
