INSERT OR IGNORE INTO Breweries (Id, Name, Address1, Address2, City, State, Code, Country, Phone, Website, Filepath, Description) VALUES ('36ec2810194949cc8679ddc60d6f213b', 'Vintage 50 Restaurant and Brew Lounge', '50 Catoctin Ct. NE #100', '', 'Leesburg', 'Virginia', '20176', 'United States', '(703) 777-2169', 'http://www.vintage50.com/', '', 'Vintage 50, at this Leesburg, Virginia classically modern brewery.  Whether you enjoy a fine California cabernet paired with innovative New American fare, or a cold beer brewed on-site paired with classic comfort food, you will feel at home at Vintage 50. Guests will relax in style while enjoying libations from near and far as well as a menu that features fresh, seasonal ingredients.');
