INSERT OR IGNORE INTO Breweries (Id, Name, Address1, Address2, City, State, Code, Country, Phone, Website, Filepath, Description) VALUES ('44a1728e210343b6b170b1613e173e4a', 'Capital City Brewing Company', '1100 New York Ave, NW', '', 'Washington', 'District of Columbia', '20005', 'United States', '202.628.2222', 'http://www.capcitybrew.com', '', 'Washington DC''s first brewpub since prohibition, Capitol City Brewing Co. opened its doors in 1992. Our first location still stands in Downtown DC, at 11th and H St., NW. Our company policy is to bring the fine craft of brewing to every person who lives and visits our region, as well as treating them to a wonderful meal and a great experience.');
