INSERT OR IGNORE INTO Breweries (Id, Name, Address1, Address2, City, State, Code, Country, Phone, Website, Filepath, Description) VALUES ('66c50bffcdcd4bb7b80ef27966940cf1', 'River Horse Brewing Company', '80 LAMBERT LANE', '', 'Lambertville', 'New Jersey', '8530', 'United States', '', 'http://www.riverhorse.com', '', 'While we might be new to your area, we have been brewing fine craft ales and lagers along the banks of the Delaware River since April of 1996. You can find our all natural, fresh bottled and draft beer products throughout the Mid-Atlantic region and parts of New England. Distribution includes Pennsylvania, New Jersey, Delaware, Maryland, New York, New York City, Long Island, Ohio, Connecticut and Massachusetts. We have also been featured by several well-known beer-of-the-month clubs. We use choice, all-natural ingredients and local spring water to produce the best product made in our area. You will notice that all of our products are pure representations of their respective styles, very clean and very well balanced.');
