INSERT OR IGNORE INTO Breweries (Id, Name, Address1, Address2, City, State, Code, Country, Phone, Website, Filepath, Description) VALUES ('7d033dca78b44799acb94c1eaae6725f', 'Bluegrass Brewing Company, Inc.', '3929 Shelbyville Rd.', '', 'Louisville', 'Kentucky', '40207', 'United States', '502-899-7070', 'http://www.bbcbrew.com', '', 'The original Bluegrass Brewing Company opened in 1993 at this location, 3929 Shelbyville Road, near Westport Rd., & Chenoweth, & Breckenridge Lanes. It is the city''s oldest remaining brewpub and was the first to make a significant mark on the national brewpub and microbrewery scene. Louisville''s best beer is made at this location, and large glass windows in the back dining room allow you to witness the brewing operation. The headbrewer is Jerry Gnagy, assisted by Sam Cruz and Cody Mingus.');
INSERT OR IGNORE INTO Breweries (Id, Name, Address1, Address2, City, State, Code, Country, Phone, Website, Filepath, Description) VALUES ('7d1f0597c5004271bd948776dc707e0b', 'Coronado Brewing Company', '170 Orange Avenue', '', 'Coronado', 'California', '92118', 'United States', '1-619-437-4452', 'http://www.coronadobrewingcompany.com/', '', 'The Coronado Brewing Company, in beautiful Coronado (located just a few minutes from downtown San Diego) welcomes you to come and enjoy quality handcrafted beers and delicious food in a casual family environment. Join us for a refreshing pint while we make you one of our signature pizzas or calzones in the wood fired oven, or choose from a wide selection of fresh seafood, steaks, pastas and dining specials. The Coronado Brewing Company is perfect for a quick lunch, a relaxing beverage or a wonderful dinner. If you''re a sports fan come relax with a cold beer and an appetizer while watching your favorite team on one of our many HD TV''s. Open 7 days a week from 11:00 am (until closing) & offering a weekly happy hour (M-F) from (2-5pm).');
