INSERT OR IGNORE INTO Breweries (Id, Name, Address1, Address2, City, State, Code, Country, Phone, Website, Filepath, Description) VALUES ('92ecbfbe736e49378d8aae2062a441c0', 'Miller Brewing', '3939 W. Highland Blvd', '', 'Milwaukee', 'Wisconsin', '53208', 'United States', '(414) 931-2000', 'http://www.millerbrewing.com', '', 'Making the highest quality beer has been a passion of the Miller Brewing Company since its founder, Frederick J. Miller, began his brewing business in 1855. Since then, Miller Brewing has grown from a small local brewer to the second largest brewery in the U.S., with seven major breweries located across America.');
INSERT OR IGNORE INTO Breweries (Id, Name, Address1, Address2, City, State, Code, Country, Phone, Website, Filepath, Description) VALUES ('930b1dc84ed34683b0e2689b069bf841', 'Marzoni''s Brick Oven & Brewing Co', '165 Patchway Road', '', 'Duncansville', 'Pennsylvania', '16635', 'United States', '814-695-2931', 'http://www.marzonis.com/', '', 'Marzoni''s beers are brewed on premise, utilizing the finest ingredients available. Beers brewed at Marzoni''s are dispensed directly from our chilled serving tanks to the tap stations on the bar for the freshest beer around! On any given day, there are at least 8 beers on tap, not to mention house-brewed root beer. Brewery tours are available!');
