INSERT OR IGNORE INTO Breweries (Id, Name, Address1, Address2, City, State, Code, Country, Phone, Website, Filepath, Description) VALUES ('c3f06ce635ee4eefbc33e6c206a31f44', 'Union Barrel Works', '6 N. Reamstown Road', '', 'Reamstown', 'Pennsylvania', '17567', 'United States', '717-335-7837', 'http://www.unionbarrelworks.com/', '', 'Union Barrel Works is a restaurant and brew pub located in Reamstown, Pennsylvania in an historic building which originally functioned as a hardware store. It later became Reamstown Garment and was a sewing factory. The building itself was built in 1911 and still has the original hardwood maple floors and authentic tin ceiling. Come visit us for a great atmosphere, better food and excellent beer!');
INSERT OR IGNORE INTO Breweries (Id, Name, Address1, Address2, City, State, Code, Country, Phone, Website, Filepath, Description) VALUES ('c3f22870b8204ec5ade70b8ce392176e', 'High Point Wheat Beer Company', '22 Park Place', '', 'Butler', 'New Jersey', '7405', 'United States', '(973) 838-7400', 'http://www.ramsteinbeer.com/', '', 'Founded in 1994, the High Point Brewing Company is the first exclusive wheat beer brewery in America. The founder and driving force of High Point is award-winning homebrewer, Greg Zaccardi. After working as a brewer in southern Germany, Greg returned to the US to open his brewery. His Ramstein beers are made with the same care and precision he learned in Germany. In fact, the wheat, barley, hops and yeast used in Ramstein are all imported directly from Bavaria.');
