INSERT OR IGNORE INTO Breweries (Id, Name, Address1, Address2, City, State, Code, Country, Phone, Website, Filepath, Description) VALUES ('d8c82baf6cf6470ea9c3a89a27f33cb9', 'Shipyard Brewing - Portland', '86 Newbury Street', '', 'Portland', 'Maine', '4101', 'United States', '1-800-789-0684', 'http://www.shipyard.com/', '', 'Since 1994, Shipyard Brewing Company has been brewing fine quality products with a wide range of style profiles. We use only top quality ingredients and pride ourselves on the consistency and freshness of our products. All our beer is hand crafted from recipes developed by master brewer Alan Pugsley, one of the most influential people in the craft brewing movement in North America.  Shipyard is available in 35+ states around the country and markets for our freshly brewed, hand-crafted beers continue to expand.');
