INSERT OR IGNORE INTO Breweries (Id, Name, Address1, Address2, City, State, Code, Country, Phone, Website, Filepath, Description) VALUES ('f9eca69a7625481b8cb96c3e665f0682', 'Anacortes Brewing', '320 Commercial Avenue', '', 'Anacortes', 'Washington', '98221', 'United States', '360-588-1720', 'http://www.anacortesrockfish.com/', '', 'Our brewers focus on high quality ingredients, a carefully controlled process and delicate handling to provide one of the best beers you will find in the industry. We operate a seven barrel brewery designed and installed by Sound Brewing Systems of Seattle with vessels fabricated by Ripley Stainless in British Columbia. Our brewery features a cold room specially designed for lagering to provide our beers with a traditional character and complexity.');
